    @SuppressWarnings("static-method")
    @Test
    public void testGetYieldTimeTrial()
    {
        long time1 = 0;
        long time2 = 0;

        for( int i = 0; i < 1000; i++ ) {
            time1 = System.nanoTime();
            double yield = BondMath.getYield(
                    0.5, LocalDate.of(2017, 4, 30), LocalDate.of(2015, 4, 30), LocalDate.of(2015, 10, 31), 4, 99.91796875,
                    LocalDate.of(2015, 5, 19)
            );
            time2 = System.nanoTime();
            Assert.assertEquals(0.54231, yield, 00.1);
        }

        System.out.println((time2 - time1)/1000.0 + " micros");
    }

